class Parent {

    protected void method1(String message) {
        System.out.println("Parent's method1 says " + message);
    }

    public void method2() {
        System.out.println("Parent's method2");
        method1("Hello World!");
    }
}

class Child extends Parent {

    public void method1(Object message) {
        super.method1(message.toString());
        System.out.println("Child's method1 says " + message);
    }

    public static void main(String args[]) {
        Parent p = new Child();
        p.method2();
    }
}

class MyThread implements Runnable {

    String myString = "Yes ";

    public void run() {
        this.myString = "No ";
    }

    public static void main(String[] args) {
        MyThread t = new MyThread();
        new Thread(t).start();
        for (int i = 0; i < 10; i++)
            System.out.print(t.myString);
    }
}

